var buyButtons = document.querySelectorAll(".buy"), buyPopup = document.querySelector(".buy_add-modal"), buyClose = document.querySelector(".close-buy-button"); buyButtons.forEach(function (e) { e.addEventListener("click", function (e) { e.preventDefault(), buyPopup.classList.add("modal-show") }) }), buyClose.addEventListener("click", function (e) { e.preventDefault(), buyPopup.classList.remove("modal-show") }), window.addEventListener("keydown", function (e) { 27 === e.keyCode && (e.preventDefault(), buyPopup.classList.contains("modal-show") && buyPopup.classList.remove("modal-show")) });